library(ggplot2)source("1-load_data_frame_with_lat_lon.R")plotLatLonWithColorScale = function( ) {	dataFrame <- createDataFrame( )		p <- ggplot( dataFrame, aes( x = Lon, y = Lat ) )	#p + geom_point( aes( colour = Magnitude ) )	#p + geom_point( aes( colour = Magnitude ) ) + scale_colour_gradient( low = "blue", high = "red" )	p + geom_point( aes( colour = Magnitude ) ) + scale_colour_gradient( low = "blue", high = "red" )}