library(ggplot2)source("1-load_data_frame_with_lat_lon.R")plotLatLonWithSizeScale = function( ) {	dataFrame <- createDataFrame( )		p <- ggplot( dataFrame, aes( x = Lon, y = Lat ) )	#p + geom_point( aes( size = Magnitude ) )	#p + geom_point( aes( size = Magnitude ), alpha = 1/10 )	#p + geom_point( aes( size = Magnitude ), alpha = 1/10 )	#p + geom_point( aes( size = Magnitude ), alpha = 1/10 ) + scale_size( range = c(1,20) )	#p + geom_point( colour = "red", aes( size = Magnitude, alpha = Magnitude ) ) + scale_size( range = c(1,7) )	p + geom_point( colour = "red", aes( size = Magnitude, alpha = Magnitude ) ) + scale_size( range = c(1,7) ) + scale_alpha( range = c(0.2,0.6) )		}