library(ggplot2)source("1-load_data_frame_with_lat_lon.R")# http://stackoverflow.com/questions/7455046/how-to-make-graphics-with-transparent-background-in-r-using-ggplot2plotLatLonWithTransparentBackground = function( ) {	dataFrame <- createDataFrame( )		p <- ggplot( dataFrame, aes( x = Lon, y = Lat ) )	p <- p + geom_point( colour = "red", aes( size = Magnitude, alpha = Magnitude ) ) + scale_size( range = c(1,7) ) + scale_alpha( range = c(0.2,0.6) )	p <- p + opts( panel.background = theme_blank( ),				   panel.grid.minor = theme_blank( ),				   panel.grid.major = theme_blank( ),				   plot.background = theme_blank( ) )	p	return(p)}