library(ggplot2)source("5-transparent_background.R")plotLatLonNoAxisOrLabels = function() {	p <- plotLatLonWithTransparentBackground()	p <- p + opts(			 axis.line=theme_blank(),              axis.text.x=theme_blank(),              axis.text.y=theme_blank(),              axis.ticks=theme_blank(),               axis.title.x=theme_blank(),              axis.title.y=theme_blank(),			 			 legend.position = "none"			 )		png( "7-no_axis_or_labels.png", width = 400, height = 400, units = "px", bg = "transparent" )	print( p )	dev.off()}